    useEffect(() => {
        if (localStorage.getItem('accessToken')) {
          setToken(localStorage.getItem('accessToken'))
        }
      }, [])

      useEffect(() => {
          if (searchTerm) {
            handleSearchRequest(searchTerm)
          }
      })

    const handleSearchRequest = (searchTerm) => {
        axios
