import React, { useState } from 'react'
import axios from 'axios'

// const searchEndpoint = 'https://api.spotify.com//v1/search'
const spaceDelimiter = '%20'

function SearchResults({ passedSearchTerm }) {
const [data, setData] = useState()

  console.log(`This is the searchTerm`, { passedSearchTerm })
  const token = localStorage.getItem('accessToken')

  const getSearchResults = () => {

    axios
      .get(
        `https://api.spotify.com/v1/search?q=${passedSearchTerm}&type=show&market=US&limit=10&offset=5`,
        {
          headers: {
            Authorization: 'Bearer ' + token,
          },
        }
      )
      .then((response) => {
        console.log(`This is the response from the api call ðŸ‘‰ `, response.data)
      })
      .catch((err) => {
        console.error('there was an error fetching podcasts', err)
      })
  }

  if (token) {
    getSearchResults()
  }


  return (
    <div>
      <h1>This is where the search results would go</h1>
    </div>
  )
}

export default SearchResults
