import React, { useState, useEffect } from 'react'
import axios from 'axios'
import SearchCard from '../components/SearchCard'
import '../styles/App.css'

function SearchPage() {
  const [searchTerm, setSearchTerm] = useState('')
  const [results, setResults] = useState({})
  const token = localStorage.getItem('accessToken')

  const handleSearch = () => {
    const string = searchTerm.split(' ').join('%20')

    axios
      .get(
        `https://api.spotify.com/v1/search?q=${string}&type=show&market=US&limit=10&offset=5`,
        {
          headers: {
            Authorization: 'Bearer ' + token,
          },
        }
      )
      .then((response) => {
        setResults(response.data.shows)
      })
      .catch((err) => {
        console.error('there was an error fetching podcasts', err)
      })

    }
    console.log(`These are these are the search results back from the API`, results)



  return (
    <div >
      <h1>Welcome to the results page</h1>
      <div className="search-bar-container is-flex">
        <div className="fa fa-search  navbar-item" id="search-bar"></div>
        <input
          type="text"
          placeholder="Search for a podcast"
          className="search-bar"
          spellcheck="false"
          onChange={(event) => {
            setSearchTerm(event.target.value)
          }}
        />
        <button className="button is-info" onClick={handleSearch}>
          Search
        </button>
        <div className="fa fa-times navbar-item"></div>
      </div>
      <div className="results-page">
      {results?.items
        ? results.items.map((item) => (
            <SearchCard key={item[0]} {...item} />
          ))
        : null}
        </div>
    </div>
  )
}

export default SearchPage
